pub fn unicodeToLower(cp: u21) u21 {
    for (&to_lower_ranges) |r| {
        if (cp < r.from) break;
        if (cp <= r.to and (cp - r.from) % r.step == 0)
            return @intCast(@as(i32, cp) + r.offset);
    }
    return cp;
}

pub fn unicodeToUpper(cp: u21) u21 {
    for (&to_upper_ranges) |r| {
        if (cp < r.from) break;
        if (cp <= r.to and (cp - r.from) % r.step == 0)
            return @intCast(@as(i32, cp) + r.offset);
    }
    return cp;
}
